import React,{useState} from 'react'
import axios from 'axios'




function Display({ displayList }){

    const listSize = displayList.length

    const nameList =  displayList.map( (element) => <li>  {element.name} </li>  )
   // const languageList = displayList.languages.map( (element) => <li>  {element.name} </li>  )


    // conditional rendering based on the list length

    //first condition
    if( listSize > 10){
        return (
            <div>
                Too many matches,specify another filter
            </div>
        )
    }


    //second condition

    if( listSize <= 10 && listSize !== 1){
        return (
            <ul>
                {nameList}
            </ul>
        )
    }

    //third condition

    return (
        <div>
            <h1> displayList.name </h1> <br/><br/>
            capital {displayList.capital} <br/>
            population {displayList.population} <br/>
            <h3> languages </h3>
            <div>     {displayList.flag}           </div>

        </div>
    )
}



function App(){

    const [searchString,setSearchString] = useState('')
    const [displayList,setDisplayList] = useState([])


    function setAfterSubmission(event){
        event.preventDefault()

        const promise = axios.get(`https://restcountries.eu/rest/v2/name/${searchString}`)
        promise.then (  (response) =>{
            console.log("promise fulfilled")
            setDisplayList.concat(response.data)  })

    }


    function handleSearchString(event){
        event.preventDefault()
        setSearchString(event.target.value)
    }



    return (
        <div>
            <form onSubmit = {setAfterSubmission}>
                find countries<br/>
                <input value = {searchString} onChange={handleSearchString}/>
            </form>
            <Display displayList = {displayList}  />
        </div>
    )
}


export default App;
