import React,{useState,useEffect} from "react";
import axios from 'axios'



function Display( {newInput,allData} ){


    const [searchResult,setSearchResult] = useState([])

    const requiredData = allData.filter (  data => data.name.toLowerCase().search(newInput.toLowerCase()) !== -1 )

    setSearchResult(searchResult.concat(requiredData))


    if(searchResult.length > 10){
    return (
     <div>   
        Too many matches,specify another filter
     </div>   
    )

    }


    if(searchResult.length <= 10) {
        return (
                <ul>  
                    searchResult.map( (element) => <li> {element.name}  </li>    )
                </ul>
        )
    }



    return (
        <>
        </>
    )

}












function App() {

    const [newInput,setNewInput] = useState('')
    let allData = []



    const promise = axios.get('https://restcountries.eu/rest/v2/all')

    promise.then( (response) => {
        allData = response.data
    } )



    function handleNewInput(event){
        setNewInput(event.target.value)
    }

    function handleSubmit(event){
        event.preventDefault()

    }



  return (
    <div>
       <form onSubmit = "handleSubmit"> 
        find countries 
        <input value = {newInput} onChange = {handleNewInput}/>
      </form>
    </div>
  );
}

export default App;
